import React from "react";
import { formatPrice } from "../helpers";

class EditFishForm extends React.Component {
  render() {
    return (
      <div className="fish-edit">
        <input
          type="text"
          name="name"
          value={JSON.stringify(this.props.fish.name)}
        />
        <input
          type="text"
          name="price"
          value={formatPrice(this.props.fish.price)}
        />
        <select name="status" value={this.props.fish.status}>
          <option value="available">Available</option>
          <option value="sold-out">Sold Out!</option>
        </select>
        <textarea name="desc" value={JSON.stringify(this.props.fish.desc)} />
        <input type="text" name="image" value={this.props.fish.image} />
      </div>
    );
  }
}

export default EditFishForm;
